typedef long long ll;
typedef pair<int,int> PII;
typedef vector<PII> VII;
typedef vector<int> VI;
const int maxn=50010;
//ll size[maxn];
ll size[maxn*4];
ll maxx[maxn*4];
int rev[maxn*4];
#define test printf("ok\n");
void pushup(int cur)
{
	size[cur]=size[cur<<1|1]+size[cur<<1];
	maxx[cur]=max(maxx[cur<<1],maxx[cur<<1|1]);
	return ;
}
void build(int l,int r,int cur)
{
//	test;
	size[cur]=maxx[cur]=0;
//	rev[cur]=0;
	if(l>r)return;
	if(l==r)
	{
		size[cur]=1;
		maxx[cur]=1;
		return;
	}
	int mid=(l+r)/2;
	build(l,mid,cur<<1);
	build(mid+1,r,cur<<1|1);
	pushup(cur);
}
void pushdown(int cur)
{
	if(size[cur<<1]+size[cur<<1|1]<size[cur])
	{
		size[cur<<1]*=(ll)2;
		maxx[cur<<1]*=(ll)2;
		maxx[cur<<1|1]*=(ll)2;
		size[cur<<1|1]*=(ll)2;
	}
}
void update(int curl,int curr,int l,int r,int cur)
{
	//printf("%d  %d\n",curl,curr);
	if(curr<curl)return;
	if(l==r)
	{
		test;
		//printf("%d  %d\n",curl,curr);
		size[cur]+=(curr-curl+1);
		maxx[cur]=max(maxx[cur],size[cur]);
		return;
	}
	else if(curl==1&&curr==size[cur])
	{
		size[cur]*=(ll)2;
		maxx[cur]*=2;
		return;
	}
	pushdown(cur);
	int mid=(l+r)/2;
	if(curl<=size[cur<<1]&&curr<=size[cur<<1])
	{
	//	test;
		update(curl,curr,l,mid,cur<<1);
	}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
	else if(curl>size[cur<<1]&&curr>size[cur<<1])
	{
		update(curl-size[cur<<1],curr-size[cur<<1],mid+1,r,cur<<1|1);
	}
	else if(curl<=size[cur<<1]&&curr>size[cur<<1])
	{
		update(curl,size[cur<<1],l,mid,cur<<1);
		update(1,curr-size[cur<<1],mid+1,r,cur<<1|1);
	}
	pushup(cur);
}
ll query(int curl,int curr,int l,int r,int cur)
{
//	if(curl>r||curr<l) return 0;
//	test;
	if(l==r)
	{
		return curr-curl+1;
	}
	else if(curl==1&&curr==size[cur])
	{
		return maxx[cur];
	}
	pushdown(cur);
	int mid=(l+r)/2;
	ll ret1=0,ret2=0;
	if(curl<=size[cur<<1]&&curr<=size[cur<<1])
	{
		ret1=query(curl,curr,l,mid,cur<<1);
		ret2=0;
	}
	else if(curl>size[cur<<1]&&curr>size[cur<<1])
	{
		ret1=0;
		ret2=query(curl-size[cur<<1],curr-size[cur<<1],mid+1,r,cur<<1|1);
	}
	else if(curl<=size[cur<<1]&&curr>size[cur<<1])
	{
		ret1=query(curl,size[cur<<1],l,mid,cur<<1);
		ret2=query(1,curr-size[cur<<1],mid+1,r,cur<<1|1);
	}
	return max(ret1,ret2);
}
		
		
		
